import React, { useState } from "react";
import Header from "./components/Header";
import TaskList from "./components/TaskList";
import AddTask from "./components/AddTask";
import "./App.css";
import DeleteTask from "./components/DeleteTask";
const App = () => {
  const [tasks, setTasks] = useState([
    // {
    //   id: "1",
    //   taskName: "jkvbhjbhjbhjbjbjh",
    //   deadline: "dc",
    //   status: "safc",
    // },
  ]);

  const handleAddTask = (newTask) => {
    const newEntry = {
      id: tasks.length + 1,
      taskName: newTask.taskName,
      deadline: newTask.deadline,
      status: false,
    };

    setTasks((prevTasks) => [...prevTasks, newEntry]);
  };

  const toggleTaskCompletion = (id) => {
    const updatedTasks = tasks.map((task) =>
      id === task.id ? { ...task, status: !task.status } : task
    );
    setTasks(updatedTasks);
  };

  const handleDeleteTask = (taskToDelete) => {
    const updatedTasks = tasks.filter(task => (
      task.id !== Number(taskToDelete.taskId)
    ));

    let counter=0;

    setTasks(updatedTasks);
    console.log(tasks);
  }

  return (
    <div>
      <Header />
      {tasks.length ? (
        <TaskList tasks={tasks} onToggle={toggleTaskCompletion} />
      ) : (
        <h2 className="center">Add Tasks!</h2>
      )}
      <AddTask onAdd={handleAddTask} />
      <DeleteTask onDelete={handleDeleteTask} tasks={tasks}/>
    </div>
  );
};

export default App;
