import React, { useState } from "react";
import "../App.css";

const AddTask = ({ onAdd }) => {
  const [newTask, setNewTask] = useState({ taskName: "", deadline: "", deadlineError: "" });

  const handleChange = event => {
    const { name, value } = event.target;
    setNewTask((prevTask) => ({
      ...prevTask,
      [name]: value,
    }));
  };

  const handleSubtmit = event => {
    event.preventDefault();
    if (!newTask.taskName || !newTask.deadline) {
      return ;
    }
    onAdd(newTask);
    setNewTask({ taskName: "", deadline: "" });
  };

  return (
    <div className="center">
      <form>
        <input
          name="taskName"
          value={newTask.taskName}
          placeholder="Add New Task"
          onChange={handleChange()}
        />
        <input
          name="deadline"
          value={newTask.deadline}
          placeholder="Add deadline"
          onChange={handleChange()}
        />
        <div style={{color:"red", fontSize:12}}>newTask.deadlineError</div>
        <button type="submit" onSubmit={handleSubtmit()}>Add New Task!</button>
      </form>
    </div>
  );
};

export default AddTask;
