import React, { useState } from "react";
import "../App.css";

const AddTask = ({ onAdd }) => {
    const[newTask, setNewTask] = useState({taskName: "", deadline: ""});

    const handleChange = event => {
        const {name, value} = event.target;
        setNewTask(prevTask => ({
            ...prevTask,
            [name]: value
        }));
    }

    return (
    <div className="center">
      <form>
        <h3>Add Tasks here!</h3>
        <input name="taskName" value={newTask.taskName} placeholder="Add New Task" onChange={handleChange()}/>
        <input name="taskName" value={newTask.taskName} placeholder="Add New Task" onChange={handleChange()}/>
      </form>
    </div>
  );
};

export default AddTask;
