import React, { useState } from "react";
import "../App.css";
import { isValidDateValue } from "@testing-library/user-event/dist/utils";

const AddTask = ({ onAdd }) => {
  const [newTask, setNewTask] = useState({
    taskName: "",
    deadline: "",
    error: "",
  });

  const handleChange = (event) => {
    const { name, value } = event.target;
    setNewTask((prevTask) => ({
      ...prevTask,
      [name]: value,
    }));
  };

  const isValid = () => {
    const days = ['monday', 'tuesday', 'wednesday', 'friday', 'thursday', 'saturday', 'sunday'];
    let error = "";

    if(!days.includes(newTask.deadline.toLowerCase)) {
        error = "Enter v"
    }
  }

  const handleSubtmit = (event) => {
    event.preventDefault();
    if (!newTask.taskName || !newTask.deadline) {
      return;
    } else if (isValid()) {
      onAdd(newTask);
      setNewTask({ taskName: "", deadline: "" });
    }
  };

  return (
    <div className="center">
      <form>
        <input
          name="taskName"
          value={newTask.taskName}
          placeholder="Add New Task"
          onChange={handleChange()}
        />
        <input
          name="deadline"
          value={newTask.deadline}
          placeholder="Add deadline"
          onChange={handleChange()}
        />
        <div style={{ color: "red", fontSize: 12 }}>newTask.error</div>
        <button type="submit" onSubmit={handleSubtmit()}>
          Add Task
        </button>
      </form>
    </div>
  );
};

export default AddTask;
