import React, { useState } from "react";
import "../App.css";

const DeleteTask = ({ onDelete, tasks }) => {
    const[taskToDelete, setTaskToDelete] = useState({taskId: "", error: ""});

    const handleChange = (event) => {
        setTaskToDelete(prevTask => ({
            ...prevTask,
            taskId: event.target.value,
        }))
    }

    const isValid = () => {
        tasks.map(task => task.id === taskToDelete.taskId ? taskToDelete.error="" : taskToDelete.error="Enter correct")
    }

    const handleSubmit = (event) => {
        event.preventDefault();
        if(isValid()) {
            onDelete(taskToDelete);
            setTaskToDelete({ taskId:"", error:"" })
        }
    }

  return (
    <div className="center">
      <form onSubmit={handleSubmit}>
        <h3 className="center">Add Tasks here!</h3>
        <input
          name="taskId"
          value={task.taskId}
          placeholder="Add New Task"
          onChange={handleChange}
        />
        <div className="center">
            <button type="submit">
            Add Task
            </button>
        </div>
      </form>
    </div>
  );
};

export default DeleteTask;
