import React, { useState } from "react";
import "../App.css";

const AddTask = ({ onAdd }) => {
  const [newTask, setNewTask] = useState({
    taskName: "",
    deadline: "",
    error: "",
  });

  const handleChange = (event) => {
    const { name, value } = event.target;
    setNewTask((prevTask) => ({
      ...prevTask,
      [name]: value,
    }));
  };

  const isValid = () => {
    const days = [
      "monday",
      "tuesday",
      "wednesday",
      "friday",
      "thursday",
      "saturday",
      "sunday",
    ];
    let error = "";
    if (!newTask.taskName || !newTask.deadline) {
        error = "Please fill all fields";
    }
    else if (!days.includes(newTask.deadline.toLowerCase())) {
      error = "Enter valid day of the week";
    }

    if (error) {
      setNewTask((prevTask) => ({
        ...prevTask,
        error: error,
      }));
    }

    return error === "";
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    if (isValid()) {
      onAdd(newTask);
      console.log()
      setNewTask({ taskName: "", deadline: "", error: "" });
    }
  };

  return (
    <div className="center">
      <form onSubmit={handleSubmit}>
        <input
          name="taskName"
          value={newTask.taskName}
          placeholder="Add New Task"
          onChange={handleChange}
        />
        <input
          name="deadline"
          value={newTask.deadline}
          placeholder="Add deadline"
          onChange={handleChange}
        />
        <div style={{ color: "red", fontSize: 12 }}>{newTask.error}</div>
        <button type="submit">
          Add Task
        </button>
      </form>
    </div>
  );
};

export default AddTask;
